SELECT USED_GOODS_BOARD.TITLE AS TITLE, USED_GOODS_BOARD.BOARD_ID, USED_GOODS_REPLY.REPLY_ID, USED_GOODS_REPLY.WRITER_ID, USED_GOODS_REPLY.CONTENTS, DATE_FORMAT(USED_GOODS_REPLY.CREATED_DATE, '%Y-%m-%d') AS DATE_FORMAT FROM USED_GOODS_BOARD RIGHT JOIN USED_GOODS_REPLY ON USED_GOODS_REPLY.BOARD_ID = USED_GOODS_BOARD.BOARD_ID WHERE USED_GOODS_BOARD.CREATED_DATE BETWEEN '2022-10-01' AND '2022-10-31' ORDER BY DATE_FORMAT, TITLE